(974, 67, '_faq_page_section_title', 'field_5ddaa0f150b40'),
(975, 67, 'faq_items_0_faq_question', 'How big are your booths?'),
(976, 67, '_faq_items_0_faq_question', 'field_5ddaa16450b42'),
(977, 67, 'faq_items_0_faq_answer', '74” tall X 48” long X 29” deep. It is a real traditional photo booth that use to be setup in a mall.'),
(978, 67, '_faq_items_0_faq_answer', 'field_5ddaa18b50b43'),
